import React, { useState } from "react";
import { connect } from "react-redux";

import Button from "./Button";
import EditForm from "./Form";
import Modal from "./Modal";

const handleDelete = () => {
  console.log("Delete");
};

const handleOnReOpen = () => {
  console.log("handleOnReOpen");
};

const handleDone = () => {
  console.log("handleDone");
};

const TableList = ({ todos }) => {
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const [deleteModalIsOpen, setDeleteModalIsOpen] = useState(false);

  const handleEdit = () => {
    setEditModalIsOpen(true);
  };

  const onEditDismiss = () => {
    setEditModalIsOpen(false);
  };

  const handleTableRowClick = (e, postId) => {
    e.persist();
    e.stopPropagation();
    console.log("eveTarget", e.target);
    console.log(postId);
  };

  const renderTableRow = todos => {
    return todos.map((todo, index) => (
      <tr key={index} onClick={e => handleTableRowClick(e, todo.id)}>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.title}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.priority}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.createdAt}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.dueDate}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {actions(todo.currentState)}
        </td>
      </tr>
    ));
  };

  const actions = currentState => {
    if (currentState)
      return (
        <div className="todo-item action">
          <Button name={"Re-Open"} class_="re-open" onClick={handleOnReOpen} />
        </div>
      );
    return (
      <div className="todo-item action">
        <Button name="Edit" class_="edit" onClick={handleEdit} />
        <Button name="Delete" class_="delete" onClick={() => handleDelete()} />
        <Button name="Done" class_="done" onClick={handleDone} />
      </div>
    );
  };

  return (
    <>
      <tbody>{renderTableRow(todos)}</tbody>
      <Modal
        show={editModalIsOpen}
        onDismiss={onEditDismiss}
        title="Edit"
        content={<EditForm />}
        actions={<Button name="Save" />}
      />
    </>
  );
};

const mapStateToProps = state => {
  return {
    todos: state.todos
  };
};

export default connect(mapStateToProps)(TableList);
