import React, { useState } from "react";

const EditForm = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [selectedOption, setSelectedOption] = useState("option1");
  const [priority, setPriority] = useState("none");
  const handleOptionChange = ev => {
    setSelectedOption(ev.target.value);
  };

  const handlePriorityChange = ev => {
    console.log(ev);
    setPriority(ev.target.value);
  };

  return (
    <div>
      <form>
        <div>
          <label>
            Title:
            <input
              type="text"
              value={title}
              onChange={e => setTitle(e.target.value)}
            />
          </label>
        </div>
        <div>
          <label>
            Description:
            <textarea
              value={description}
              onChange={e => setDescription(e.target.value)}
              rows="10"
              cols="30"
            ></textarea>{" "}
          </label>
        </div>
        <div className="current-state">
          <div className="radio">
            <label>
              <input
                type="radio"
                value="true"
                checked={selectedOption === "true"}
                onChange={e => handleOptionChange(e)}
              />
              Closed
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="false"
                checked={selectedOption === "false"}
                onChange={e => handleOptionChange(e)}
              />
              Open
            </label>
          </div>
        </div>
        <div>
          <label>Created At:</label>
        </div>
        <div>
          <label>DueDate</label>
        </div>

        <div className="priority">
          <div className="radio">
            <label>
              <input
                type="radio"
                value="low"
                checked={priority === "low"}
                onChange={e => handlePriorityChange(e)}
              />
              Low
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="medium"
                checked={priority === "medium"}
                onChange={e => handlePriorityChange(e)}
              />
              Medium
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="high"
                checked={priority === "high"}
                onChange={e => handlePriorityChange(e)}
              />
              High
            </label>
          </div>
          <div className="radio">
            <label>
              <input
                type="radio"
                value="none"
                checked={priority === "none"}
                onChange={e => handlePriorityChange(e)}
              />
              None
            </label>
          </div>
        </div>
      </form>
    </div>
  );
};

export default EditForm;
