import React, { useState } from "react";
import { connect } from "react-redux";

import Button from "./Button";
import EditForm from "./Form";
import Modal from "./Modal";

import { deleteTodo } from "../store/todo";

const handleOnReOpen = () => {
  console.log("handleOnReOpen");
};

const handleDone = () => {
  console.log("handleDone");
};

const TableList = ({ todos }) => {
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const [deleteModalIsOpen, setDeleteModalIsOpen] = useState(false);
  let itemToBeDeleted = null;

  const handleDelete = postId => {
    console.log("Delete");
    setDeleteModalIsOpen(true);
    itemToBeDeleted = postId;
    console.log(itemToBeDeleted);
  };

  const onDeleteConfirmation = () => {
    console.log("ok you may delete");
  };

  const handleEdit = () => {
    // setEditModalIsOpen(true);
  };

  const onEditDismiss = () => {
    setEditModalIsOpen(false);
  };

  const handleTableRowClick = (e, postId) => {
    e.persist();
    e.stopPropagation();
    if (e.target.matches(".delete")) {
      handleDelete(postId);
    } else if (e.target.matches(".done")) {
      handleDone(postId);
    } else if (e.target.matches(".edit")) {
      handleEdit(postId);
    } else if (e.target.matches(".re-open")) {
      handleOnReOpen();
    } else {
      console.log("Modal desc to be opened");
      // openDescriptionModal()
    }
  };

  const renderTableRow = todos => {
    return todos.map((todo, index) => (
      <tr key={index} onClick={e => handleTableRowClick(e, todo.id)}>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.title}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.priority}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.createdAt}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.dueDate}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {actions(todo.currentState)}
        </td>
      </tr>
    ));
  };

  const actions = currentState => {
    if (currentState)
      return (
        <div className="todo-item action">
          <Button name={"Re-Open"} class_="re-open" />
        </div>
      );
    return (
      <div className="todo-item action">
        <Button name="Edit" class_="edit" />
        <Button name="Delete" class_="delete" />
        <Button name="Done" class_="done" />
      </div>
    );
  };

  return (
    <>
      <tbody>{renderTableRow(todos)}</tbody>
      <Modal
        show={editModalIsOpen}
        onDismiss={onEditDismiss}
        title="Edit"
        content={<EditForm />}
        actions={<Button name="Save" />}
      />
      <Modal
        show={deleteModalIsOpen}
        onDismiss={() => setDeleteModalIsOpen(false)}
        title="Delete"
        content={<p>Are you sure you want to Delete</p>}
        actions={
          <>
            <Button
              name="Yes! I'm sure"
              onClick={onDeleteConfirmation}
              class_="delete"
            />
            <Button
              name="No! Keep it"
              onClick={() => setDeleteModalIsOpen(false)}
              class_="warning"
            />
          </>
        }
      />
    </>
  );
};

const mapStateToProps = state => {
  return {
    todos: state.todos
  };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteTodo: postId => dispatch(deleteTodo(postId))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(TableList);
