import dummyData from "../dummy/data";

const ADD_TODO = "ADD_TODO";
const EDIT_TODO = "EDIT_TODO";
const DELETE_TODO = "DELETE_TODO";
const MARK_DONE = "MARK_DONE";
const RE_OPEN = "RE_OPEN";

const initialState = dummyData;

export const addTodo = data => {
  return {
    type: ADD_TODO,
    payload: data
  };
};

export const reOpenTodo = id => {
  console.log("action");
  return {
    type: RE_OPEN,
    payload: id
  };
};

export const deleteTodo = id => {
  return {
    type: DELETE_TODO,
    payload: id
  };
};

const TodoReducer = (state = initialState, action) => {
  console.log("Entering");
  switch (action.type) {
    case ADD_TODO:
      return [action.payload, ...state];

    case DELETE_TODO:
      return state.filter(todo => todo.id !== action.payload);

    case RE_OPEN:
      console.log("Hi");
      const itemIndex = state.findIndex(todo => todo.id === action.payload);
      console.log("In reducer", itemIndex);
      const tempObj = { ...state[itemIndex], currentState: true };
      state[itemIndex] = tempObj;
      const newState = [
        ...state.slice(0, itemIndex),
        tempObj,
        ...state.splice(itemIndex + 1)
      ];

      // console.log(tempObj);
      return newState;

    default:
      return state;
  }
};

export default TodoReducer;
