const SORT_BY_SUMMARY = "SORT_BY_SUMMARY";
const SORT_BY_PRIORITY = "SORT_BY_PRIORITY ";
const SORT_BY_CREATED_ON = "SORT_BY_CREATED_ON";
const SORT_BY_DUE_DATE = "SORT_BY_DUE_DATE";
const SORT_BY_ACTION = "SORT_BY_ACTION";

export const sortBySummary = () => {
  return {
    type: SORT_BY_SUMMARY
  };
};

const SortReducer = (
  state = {
    sortBySummary: null,
    sortByPriority: null,
    sortByCreatedOn: null,
    sortByDueDate: null,
    sortByAction: null
  },
  action
) => {
  switch (action.type) {
    case SORT_BY_SUMMARY:
      if (state.sortBySummary === null) {
        return { ...state, sortBySummary: 1 };
      } else return { ...state, sortBySummary: !state.sortBySummary };

    default:
      return state;
  }
};

export default SortReducer;
