import React from "react";
import { connect } from "react-redux";

const TableList = ({ todos }) => {
  const actions = currentState => {
    if (currentState)
      return (
        <div className="todo-item action">
          <button className="re-open-btn button">Re-Open</button>
        </div>
      );
    return (
      <div className="todo-item action">
        <button className="edit-btn button">Edit</button>
        <button className="delete-btn button">Delete</button>
        <button className="done-btn button">Done</button>
      </div>
    );
  };

  const renderTableRow = () => {
    return todos.map(todo => (
      <tr>
        <td
          className={
            "table-data " + todo.currentState ? "complete" : "in-complete"
          }
        >
          {todo.title}
        </td>
        <td
          className={
            "table-data " + todo.currentState ? "complete" : "in-complete"
          }
        >
          {todo.priority}
        </td>
        <td
          className={
            "table-data " + todo.currentState ? "complete" : "in-complete"
          }
        >
          {todo.createdAt}
        </td>
        <td
          className={
            "table-data " + todo.currentState ? "complete" : "in-complete"
          }
        >
          {todo.dueDate}
        </td>
        <td
          className={
            "table-data " + todo.currentState ? "complete" : "in-complete"
          }
        >
          {actions(todo.currentState)}
        </td>
      </tr>
    ));
  };
  return <tbody>{renderTableRow()}</tbody>;
};

const mapStateToProps = state => {
  return {
    todos: state.todos
  };
};

export default connect(mapStateToProps)(TableList);
