import React from "react";
import { connect } from "react-redux";

import Button from "./Button";
import { renderIntoDocument } from "react-dom/test-utils";

const TableRow = ({ todos, filterBy, handleTableRowClick }) => {
  const { showAll, showPending, showCompleted } = filterBy;
  const filteredTodos = getFilteredList(todos);

  const getFilteredList = todos => {
    if (showAll) return todos;
    else if (showPending)
      return todos.filter(todo => todo.currentState === false);
    else if (showCompleted)
      return todos.filter(todo => renderIntoDocument.currentState === true);
  };

  return filteredTodos.map((todo, index) => (
    <tr key={index} onClick={e => handleTableRowClick(e, todo.id)}>
      <td
        className={
          "table-data " + (todo.currentState ? "complete" : "in-complete")
        }
      >
        {todo.title}
      </td>
      <td
        className={
          "table-data " + (todo.currentState ? "complete" : "in-complete")
        }
      >
        {todo.priority}
      </td>
      <td
        className={
          "table-data " + (todo.currentState ? "complete" : "in-complete")
        }
      >
        {todo.createdAt}
      </td>
      <td
        className={
          "table-data " + (todo.currentState ? "complete" : "in-complete")
        }
      >
        {todo.dueDate}
      </td>
      <td
        className={
          "table-data " + (todo.currentState ? "complete" : "in-complete")
        }
      >
        {actions(todo.currentState)}
      </td>
    </tr>
  ));
};

const actions = currentState => {
  if (currentState)
    return (
      <div className="todo-item action">
        <Button name={"Re-Open"} class_="re-open" />
      </div>
    );
  return (
    <div className="todo-item action">
      <Button name="Edit" class_="edit" />
      <Button name="Delete" class_="delete" />
      <Button name="Done" class_="done" />
    </div>
  );
};

const mapStateToProps = state => {
  return {
    todos: state.todos,
    filterBy: state.filter
  };
};

export default connect(mapStateToProps)(TableRow);
