import React from "react";
import { connect } from "react-redux";

import Button from "./Button";
import { showAll, showPending, showCompleted } from "../store/filter";

const Tabs = ({ showAll, showPending, showCompleted }) => {
  return (
    <div className="tab-section">
      <ul>
        <li onClick={showAll}>
          <Button name="SHOW ALL" />
        </li>
        <li onClick={showPending}>
          <Button name="PENDING" />
        </li>
        <li onClick={showCompleted}>
          <Button name="COMPLETED" />
        </li>
      </ul>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    filter: state.filter
  };
};

const mapDispatchToProps = dispatch => {
  return {
    showAll: () => dispatch(showAll()),
    showPending: () => dispatch(showPending()),
    showCompleted: () => dispatch(showCompleted())
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(Tabs);
