import React, { useState } from "react";
import { connect } from "react-redux";

import Button from "./Button";
import Modal from "./Modal";

const handleDelete = () => {
  console.log("Delete");
};

const handleOnReOpen = () => {
  console.log("handleOnReOpen");
};

const handleDone = () => {
  console.log("handleDone");
};

const TableList = ({ todos }) => {
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const [deleteModalIsOpen, setDeleteModalIsOpen] = useState(false);

  const handleEdit = () => {
    setEditModalIsOpen(true);
  };

  const onEditDismiss = () => {
    setEditModalIsOpen(false);
  };

  const renderTableRow = todos => {
    return todos.map((todo, index) => (
      <tr key={index}>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.title}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.priority}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.createdAt}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {todo.dueDate}
        </td>
        <td
          className={
            "table-data " + (todo.currentState ? "complete" : "in-complete")
          }
        >
          {actions(todo.currentState)}
        </td>
      </tr>
    ));
  };

  const actions = currentState => {
    if (currentState)
      return (
        <div className="todo-item action">
          <Button name={"Re-Open"} type="re-open" onClick={handleOnReOpen} />
        </div>
      );
    return (
      <div className="todo-item action">
        <Button name="Edit" type="edit" onClick={handleEdit} />
        <Button name="Delete" type="delete" onClick={handleDelete} />
        <Button name="Done" type="done" onClick={handleDone} />
      </div>
    );
  };

  return (
    <>
      <tbody>{renderTableRow(todos)}</tbody>
      <Modal
        show={editModalIsOpen}
        onDismiss={onEditDismiss}
        name="Edit Modal"
        content={<Button name="Save" />}
      />
    </>
  );
};

const mapStateToProps = state => {
  return {
    todos: state.todos
  };
};

export default connect(mapStateToProps)(TableList);
