const SORT_BY_SUMMARY = "SORT_BY_SUMMARY";
const SORT_BY_PRIORITY = "SORT_BY_PRIORITY ";
const SORT_BY_CREATED_ON = "SORT_BY_CREATED_ON";
const SORT_BY_DUE_DATE = "SORT_BY_DUE_DATE";
const SORT_BY_ACTION = "SORT_BY_ACTION";

export const sortBySummary = () => {
  return {
    type: SORT_BY_SUMMARY
  };
};

const initialState = {
  sortBySummary: null,
  sortByPriority: null,
  sortByCreatedOn: null,
  sortByDueDate: null,
  sortByAction: null
};

const SortReducer = (
  state = {
    sortBy: null,
    type: null
  },
  action
) => {
  const sortType = {
    true: "ascending",
    false: "descending"
  };
  switch (action.type) {
    case SORT_BY_SUMMARY:
      if (state.sortBy === "ascending") {
        return { sortBy: "summary", type: !state.type };
      } else return { sortBy: "summary", type: true };

    default:
      return state;
  }
};

export default SortReducer;
